mapper.update:
update member
set		password=#{password},
			name=#{name},
			email=#{email}
where	id=#{id}

mapper.select:
select id, password, name, email, join_date
from (
	select ceil(rownum/#{contentsPerPage}) page, id, password, name, email, join_date
	from (

			order by join_date
	)
) 
where page = #{pageNo}
